import React, { useEffect, useState } from 'react';
import axios from 'axios';
import CommentContents from './CommentContents';
import RcmVideoList from './RcmVideoList';
import { useParams } from 'react-router-dom';
import ThumbUpOffAltIcon from '@mui/icons-material/ThumbUpOffAlt';
import ThumbDownOffAltIcon from '@mui/icons-material/ThumbDownOffAlt';
import ReplyIcon from '@mui/icons-material/Reply';
import SortIcon from '@mui/icons-material/Sort';

const Video = () => {
  const { videoId } = useParams();
  const [videoDetail, setVideoDetail] = useState(null);
  const [likeCount, setLikeCount] = useState(0);

  useEffect(() => {
    const fetchVideoDetails = async () => {
      const BASE_URL = 'https://youtube-v31.p.rapidapi.com/videos';
      const options = {
        method: 'GET',
        url: BASE_URL,
        params: {
          part: 'contentDetails,snippet,statistics',
          id: videoId,
        },
        headers: {
          'X-RapidAPI-Key':
            '124beff6fcmsh4aacf917256fd61p13baa9jsneffb301e16ac',
          'X-RapidAPI-Host': 'youtube-v31.p.rapidapi.com',
        },
      };

      try {
        const response = await axios.request(options);
        console.log(response.data);
        setVideoDetail(response.data.items[0]);
      } catch (error) {
        console.error('Error fetching video details:', error);
      }
    };

    fetchVideoDetails();
  }, [videoId]);

  const handleLikeClick = () => {
    setLikeCount(likeCount + 1);
  };

  return (
    <div className='wrapper'>
      <div className='videoWrapper'>
        <iframe
          className='video'
          style={{ borderRadius: '0.8rem' }}
          frameborder='0'
          width='100%'
          height='378px'
          src={`https://www.youtube.com/embed/${videoId}`}
          title='YouTube video player'
          allow='accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share'
          allowFullScreen></iframe>
        <div className='video-info'>
          <div className='title'>
            {videoDetail ? videoDetail.snippet.title : 'Loading...'}
          </div>
          <div className='video-info-bar'>
            <div className='channel'>
              <div className='img-box'>
                <img
                  className='profile'
                  src='data:image/png;base64,'></img>
              </div>
              <div className='channel-info'>
                <a className='channel-info-title'>
                  {videoDetail
                    ? videoDetail.snippet.channelTitle
                    : 'Loading...'}
                </a>
                <p className='channel-info-subscribers'>구독자 23.4만명</p>
              </div>
              <div className='channel-info-subscribe-btn-box'>
                <button className='channel-info-subscribe-btn'>구독</button>
              </div>
            </div>
            <div className='btn'>
              <div className='btn-likeUnlike'>
                <button
                  className='btn-likeUnlike-like'
                  onClick={handleLikeClick}>
                  <ThumbUpOffAltIcon />
                  {videoDetail
                    ? videoDetail.statistics.likeCount + likeCount
                    : 'Loading...'}
                </button>
                <button className='btn-likeUnlike-unlike'>
                  <ThumbDownOffAltIcon />
                </button>
              </div>
              <div className='btn-share-box'>
                <button className='btn-share'>
                  <ReplyIcon />
                  공유
                </button>
              </div>
              <div className='btn-more-box'>
                <button className='btn-more'>・・・</button>
              </div>
            </div>
          </div>
        </div>
        <div className='detail'>
          <div className='detail-watch-date'>
            {videoDetail && (
              <>
                조회수 {videoDetail.statistics.viewCount}회{' '}
                {new Date(videoDetail.snippet.publishedAt).toLocaleDateString(
                  'ko-KR',
                  {
                    year: 'numeric',
                    month: 'numeric',
                    day: 'numeric',
                  }
                )}
              </>
            )}
          </div>
          <div className='detail-watch-detail'>
            {videoDetail ? videoDetail.snippet.description : 'Loading...'}
          </div>
        </div>
        <div className='comment'>
          <div className='comment-box'>
            <div className='comment-number'>
              {videoDetail && <>댓글 {videoDetail.statistics.commentCount}개</>}
            </div>
            <div className='comment-filter'>
              <SortIcon />
              정렬 기준
            </div>
          </div>
        </div>
        <CommentContents />
      </div>
      <div className='list-side'></div>
    </div>
  );
};

export default Video;
